var value = msg.payload / 1024.0;
msg.payload = value.toFixed(3);
return msg;

// Message from trigger
// The trigger sends true, then false after 1s
if(msg.topic == "trigger") {
    context.set('wait', msg.payload);
    if(msg.payload) {
        // Trigger started, wait...
        return null;
    }
    // Get and remove buffered value, then send it
    var buffered = context.get('buf');
    context.set('buf', null);
    return {payload: buffered};
}

// Message from external input
if(context.get('wait')) {
    // Trigger active, store value
     context.set('buf', msg.payload);
} else {
    // Trigger not active, pass on value
    return msg;
}

if(msg.topic == "brightness") {
    context.bright_delay = msg.payload;
    if(!context.bright_delay) {
        var result = context.bright_buf;
        context.bright_buf = null;
        return {payload: result};
    }
} else {
    if(context.bright_delay) {
        context.bright_buf = msg.payload;
    } else {
        return msg;
    }
}

if(msg.topic == "control/lightshow/mode/available") {
    context.lightshow_modes = msg.payload;
} else {
    if(!context.lightshow_modes) {
        return;
    }

    if(!context.lightshow_mode) {
        context.lightshow_mode = 0;
    }

    var modes = context.lightshow_modes.split(","); 

    if(msg.topic == "devices/receiver/sw2/pulse_on") {
        context.lightshow_mode--;
    } else if(msg.topic == "devices/receiver/sw2/pulse_off") {
        context.lightshow_mode++;
    }

    if(context.lightshow_mode >= modes.length) {
        context.lightshow_mode = modes.length - 1;
    }
    if(context.lightshow_mode < 0) {
        context.lightshow_mode = 0;
    }

    return { 
        payload: modes[context.lightshow_mode],
        topic: "mode" 
    }

}

if(msg.topic == "control/lightshow/brightness") {
    context.lightshow_brightness = Number(msg.payload);
} else {
    if(!context.lightshow_brightness) {
        context.lightshow_brightness = 0;
    }

    if(msg.topic == "devices/receiver/sw3/pulse_on") {
        context.lightshow_brightness -= 50;
    } else if(msg.topic == "devices/receiver/sw3/pulse_off") {
        context.lightshow_brightness += 50;
    }

    if(context.lightshow_brightness > 1000) {
        context.lightshow_brightness = 1000;
    }
    if(context.lightshow_brightness < 0) {
        context.lightshow_brightness = 0;
    }

    return { 
        payload: context.lightshow_brightness,
        topic: "brightness" 
    }

}

